var slp = require('./index.js')
  , parser = new slp.CombatLogParser("/home/gmcwhirt/test_dir")
  ;

parser.on('start read', function (arg){
  console.log("start: %s", arg);
});

parser.on('end read', function (arg){
  console.log("stop: %s", arg);
});

parser.on('error', function (err){
  throw err;
});

parser.on('data', function (obj){
  console.log(obj);
});

parser.on('debug', function (msg){
  console.log("DEBUG: %s", msg);
});

parser.start();

